from math import atan, degrees


def distance(p1=(), p2=()):
    return ((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)**0.5


def line_2d(p1=(), p2=()):
    try:
        m = (p2[1] - p1[1]) / (p2[0] - p1[0])
        b = p1[1] - m * p1[0]
        return [m, b]
    except ZeroDivisionError:
        if p2[1] == p1[1]:
            return ['']
        else:
            m = 'N.D.'
            b = 'N.D.'
            return [m, b]


